mapper.select:
(SELECT * FROM com_single_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 1 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 4) UNION (SELECT * FROM com_single_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 2 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 14) UNION (SELECT * FROM com_single_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 3 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 2)

(SELECT * FROM com_tf_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 1 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 1) UNION (SELECT * FROM com_tf_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 2 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 3) UNION (SELECT * FROM com_tf_question WHERE courseId = #{courseId,jdbcType=INTEGER} AND levels = 3 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 1)

SELECT *
FROM com_grade
WHERE studentId = #{studentId,jdbcType=INTEGER} AND courseId = #{courseId,jdbcType=INTEGER}

SELECT *
FROM com_student


        AND studentId=#{studentId,jdbcType=INTEGER}


        AND name = #{name,jdbcType=VARCHAR}


        AND password=#{password,jdbcType=VARCHAR}

SELECT DISTINCT s.studentId AS studentId, s.name AS name, s.email AS email, s.cellphone AS cellphone, s.gender AS gender, s.password AS password, s.specialtyId AS specialtyId, p.name AS specialtyName FROM com_student s, com_specialty p where s.specialtyId = p.specialtyId
AND s.studentId = #{studentId,jdbcType=INTEGER}
AND s.specialtyId IN
#{specialtyItem}

SELECT *
FROM com_student_l_another_question
WHERE courseId = #{courseId,jdbcType=INTEGER} AND studentId = #{studentId,jdbcType=INTEGER}

mapper.update:
UPDATE com_grade


        grade1 = #{grade1,jdbcType=INTEGER}


        grade2 = #{grade2,jdbcType=INTEGER}


        grade3 = #{grade3,jdbcType=INTEGER}


WHERE studentId=#{studentId,jdbcType=INTEGER} AND courseId= #{courseId,jdbcType=INTEGER}

UPDATE com_student_l_another_question
SET result = #{result,jdbcType=VARCHAR}
WHERE studentId = #{studentId,jdbcType=INTEGER} AND courseId = #{courseId,jdbcType=INTEGER} AND
      Id = #{Id,jdbcType=BIGINT}

mapper.delete:
DELETE FROM com_student


        AND studentId = #{studentId ,jdbcType=INTEGER}


        AND specialtyId IN

            #{specialtyItem}
