mapper.select:
(SELECT * FROM com_single_question WHERE courseId = #{courseId} AND levels = 1 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 4) UNION (SELECT * FROM com_single_question WHERE courseId = #{courseId} AND levels = 2 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 14) UNION (SELECT * FROM com_single_question WHERE courseId = #{courseId} AND levels = 3 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 2)

(SELECT * FROM com_tf_question WHERE courseId = #{courseId} AND levels = 1 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 1) UNION (SELECT * FROM com_tf_question WHERE courseId = #{courseId} AND levels = 2 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 3) UNION (SELECT * FROM com_tf_question WHERE courseId = #{courseId} AND levels = 3 AND section IN
#{sectionItem}
ORDER BY rand() LIMIT 1)

SELECT *
FROM com_grade
WHERE studentId = #{studentId} AND courseId = #{courseId}

SELECT *
FROM com_student


        AND studentId=#{studentId}


        AND name = #{name}


        AND password=#{password}

SELECT DISTINCT s.studentId, s.name, s.email, s.cellphone, s.gender, s.password, s.specialtyId, p.name AS specialtyName FROM com_student s, com_specialty p where s.specialtyId = p.specialtyId
AND s.studentId = #{studentId}
AND s.specialtyId IN
#{specialtyItem}

SELECT *
FROM com_student_l_another_question
WHERE courseId = #{courseId} AND studentId = #{studentId}

mapper.update:
UPDATE com_grade


        grade1 = #{grade1}


        grade2 = #{grade2}


        grade3 = #{grade3}


WHERE studentId=#{studentId} AND courseId= #{courseId}

UPDATE com_student_l_another_question
SET result = #{result}
WHERE studentId = #{studentId} AND courseId = #{courseId} AND Id = #{Id}

mapper.delete:
DELETE FROM com_student


        AND studentId = #{studentId }


        AND specialtyId IN

            #{specialtyItem}
