mapper.select:
SELECT *
FROM com_specialty


        specialtyId=#{specialtyId,jdbcType=CHAR}


        AND name=#{name,jdbcType=CHAR}

SELECT *
FROM com_course


        courseId=#{courseId,jdbcType=CHAR}


        AND name=#{name,jdbcType=CHAR}


        AND period=#{period,jdbcType=TINYINT}


        AND credit=#{credit,jdbcType=TINYINT}

SELECT t.teacherId AS teacherId ,t.name AS teacherName ,c.name AS
courseName,s.name AS specialtyName,l.starttime AS starttime,l.endtime AS
endtime
FROM com_teacher_l_course l,com_course c,com_teacher t,com_specialty s
WHERE l.teacherId=t.teacherId AND l.courseId=c.courseId AND
l.specialtyId=s.specialtyId

    AND l.teacherId=#{teacherId,jdbcType=CHAR}


    AND c.name=#{courseName,jdbcType=CHAR}


    AND s.name=#{specialtyName,jdbcType=CHAR}

mapper.delete:
DELETE l FROM com_teacher_l_course l,com_course c,com_teacher t,com_specialty s
WHERE l.teacherId=t.teacherId AND l.courseId=c.courseId AND l.specialtyId=s.specialtyId
AND t.teacherId=#{teacherId,jdbcType=CHAR} AND c.name=#{courseName,jdbcType=CHAR} AND s.name=#{specialtyName,jdbcType=CHAR}
