mapper.select:
SELECT DISTINCT s.*
FROM com_teacher_l_course l,com_teacher t,com_specialty s,com_course c
WHERE l.teacherId=t.teacherId AND l.specialtyId=s.specialtyId AND
l.courseId=c.courseId

    AND t.teacherId=#{teacherId,jdbcType=INTEGER}


    AND c.courseId=#{courseId,jdbcType=INTEGER}

SELECT DISTINCT t.teacherId AS teacherId ,t.name AS teacherName
,c.courseId AS courseId,
c.name AS courseName,s.specialtyId AS specialtyId,s.name AS
specialtyName,l.starttime AS starttime,l.endtime AS endtime
FROM com_teacher_l_course l,com_course c,com_teacher t,com_specialty s
WHERE l.teacherId=t.teacherId AND l.courseId=c.courseId AND
l.specialtyId=s.specialtyId

    AND t.teacherId=#{teacherId,jdbcType=INTEGER}


    AND c.name=#{courseName,jdbcType=VARCHAR}


    AND s.specialtyId=#{specialtyId,jdbcType=INTEGER}


    AND s.name=#{specialtyName,jdbcType=VARCHAR}

ORDER BY l.starttime ASC ,l.endtime ASC
