mapper.select:
SELECT DISTINCT s.*
FROM com_teacher_l_course l,com_teacher t,com_specialty s,com_course c
WHERE l.teacherId=t.teacherId AND l.specialtyId=s.specialtyId AND
l.courseId=c.courseId

    AND t.teacherId=#{teacherId}


    AND c.courseId=#{courseId}

SELECT t.teacherId , t.name AS teacherName ,c.courseId, c.name AS courseName, s.specialtyId, s.name AS specialtyName, l.starttime, l.endtime FROM com_teacher_l_course l,com_course c,com_teacher t,com_specialty s WHERE l.teacherId=t.teacherId AND l.courseId=c.courseId AND l.specialtyId=s.specialtyId
AND t.teacherId=#{teacherId}
AND c.name=#{courseName}
AND s.specialtyId=#{specialtyId}
AND s.name=#{specialtyName}
ORDER BY l.starttime ASC ,l.endtime ASC
