mapper.select:
SELECT e.id, e.teacherId, c.courseId, c.name AS courseName, s.specialtyId, s.name AS specialtyName, e.content1, e.date1, e.sign1, e.content2, e.date2, e.sign2, e.content3, e.date3, e.sign3, e.date4, e.sign4, e.test, e.started FROM com_examinfo e, com_course c, com_specialty s WHERE e.courseId = c.courseId AND e.specialtyId = s.specialtyId
AND e.teacherId=#{teacherId}
AND e.courseId=#{courseId}
AND e.specialtyId=#{specialtyId}

mapper.delete:
DELETE FROM com_examinfo


        teacherId=#{teacherId}


        AND courseId=#{courseId}


        AND specialtyId=#{specialtyId}
