mapper.select:
SELECT s.studentId, s.name AS studentName, c.courseId, c.name AS courseName, p.name AS specialtyName, p.specialtyId, g.grade1, g.grade2, g.grade3, g.grade4, g.total FROM com_teacher_l_course l,com_grade g,com_student s,com_specialty p,com_course c WHERE l.courseId=c.courseId AND l.specialtyId=p.specialtyId AND p.specialtyId=s.specialtyId AND s.studentId=g.studentId AND g.courseId=c.courseId
AND l.teacherId=#{teacherId,jdbcType=INTEGER}
AND l.specialtyId=#{specialtyId,jdbcType=INTEGER}
AND l.courseId=#{courseId,jdbcType=INTEGER}
ORDER BY l.specialtyId ASC

mapper.update:
UPDATE com_grade
SET grade4=#{score,jdbcType=INTEGER}
WHERE studentId = #{studentId,jdbcType=INTEGER} AND courseId =
#{courseId,jdbcType=INTEGER}
