mapper.select:
SELECT DISTINCT sq.*
FROM com_single_question sq


        AND sq.courseId=#{courseId,jdbcType=INTEGER}

SELECT DISTINCT l.id AS id,c.courseId AS courseId,c.name AS courseName,p.specialtyId AS specialtyId, p.name AS specialtyName,s.studentId AS studentId,s.name AS studentName,a.content AS content,g.grade4 AS score, a.result AS result,l.result AS studentAnswer FROM com_another_question a,com_student s,com_student_l_another_question l,com_course c,com_specialty p,com_grade g WHERE l.studentId=s.studentId AND l.courseId=c.courseId AND l.id=a.id AND g.courseId=c.courseId AND g.studentId=s.studentId AND s.specialtyId=p.specialtyId
AND l.id=#{id,jdbcType=BIGINT}
AND l.courseId=#{courseId,jdbcType=INTEGER}
AND s.specialtyId=#{specialtyId,jdbcType=INTEGER}
