mapper.select:
SELECT *
FROM com_single_question


        courseId=#{courseId,jdbcType=INTEGER}


        AND section=#{section,jdbcType=VARCHAR}

SELECT *
FROM com_tf_question


        courseId=#{courseId,jdbcType=INTEGER}


        AND section=#{section,jdbcType=VARCHAR}

mapper.update:
UPDATE com_tf_question
section=#{section,jdbcType=VARCHAR}, levels=#{levels,jdbcType=INTEGER}, content=#{content,jdbcType=VARCHAR}, result=#{result,jdbcType=INTEGER},
where id=#{id,jdbcType=INTEGER}

UPDATE com_single_question
section=#{section,jdbcType=VARCHAR}, levels=#{levels,jdbcType=INTEGER}, content=#{content,jdbcType=VARCHAR}, que1=#{que1,jdbcType=VARCHAR}, que2=#{que2,jdbcType=VARCHAR}, que3=#{que3,jdbcType=VARCHAR}, que4=#{que4,jdbcType=VARCHAR}, result=#{result,jdbcType=CHAR}
where id=#{id,jdbcType=INTEGER}

mapper.select:
SELECT l.id AS id, c.courseId AS courseId, c.name AS courseName, p.specialtyId AS specialtyId, p.name AS specialtyName, s.studentId AS studentId, s.name AS studentName, a.content AS content, g.grade4 AS score, a.result AS result, l.result AS studentAnswer FROM com_another_question a,com_student s,com_student_l_another_question l,com_course c,com_specialty p,com_grade g WHERE p.specialtyId=s.specialtyId AND s.studentId=g.studentId AND c.courseId=g.courseId AND l.courseId=c.courseId AND l.studentId=s.studentId AND l.id=a.id
AND l.id=#{id,jdbcType=BIGINT}
AND l.courseId=#{courseId,jdbcType=INTEGER}
AND s.specialtyId=#{specialtyId,jdbcType=INTEGER}
AND s.studentId=#{studentId,jdbcType=INTEGER}
