mapper.select:
SELECT *
FROM com_teacher


        id=#{id,jdbcType=INTEGER}


        AND teacherId=#{teacherId,jdbcType=INTEGER}


        AND name = #{name,jdbcType=VARCHAR}


        AND gender = #{gender,jdbcType=CHAR}


        AND position = #{position,jdbcType=VARCHAR}


        AND office = #{office,jdbcType=VARCHAR}
